    LANGUAGE plpgsql
    AS $$
DECLARE
    token VARCHAR;
BEGIN
    -- Generate a URL-safe random token
    token := encode(gen_random_bytes(32), 'base64');
    -- Replace URL-unsafe characters
    token := replace(token, '+', '-');
    token := replace(token, '/', '_');
    token := replace(token, '=', '');
    RETURN token;
END;
