--
-- Name: audit_trigger_function(); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.audit_trigger_function() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
    changed_fields JSONB;
BEGIN
    -- Calculate changed fields for UPDATE
    IF TG_OP = 'UPDATE' THEN
        SELECT jsonb_object_agg(key, to_jsonb(NEW) -> key) INTO changed_fields
        FROM jsonb_each(to_jsonb(NEW))
        WHERE to_jsonb(NEW) -> key IS DISTINCT FROM to_jsonb(OLD) -> key;
    END IF;

    INSERT INTO audit_logs (
        table_name, record_id, action, old_data, new_data, changed_fields
    ) VALUES (
        TG_TABLE_NAME,
        CASE
            WHEN TG_OP = 'DELETE' THEN OLD.id
            ELSE NEW.id
        END,
        TG_OP,
        CASE WHEN TG_OP IN ('UPDATE', 'DELETE') THEN to_jsonb(OLD) ELSE NULL END,
        CASE WHEN TG_OP IN ('INSERT', 'UPDATE') THEN to_jsonb(NEW) ELSE NULL END,
        changed_fields
    );
    
    IF TG_OP = 'DELETE' THEN
        RETURN OLD;
    ELSE
        RETURN NEW;
    END IF;
END;
$$;


ALTER FUNCTION public.audit_trigger_function() OWNER TO postgres;

--
-- Name: audit_trigger_simple(); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.audit_trigger_simple() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
BEGIN
    -- Check if audit_logs table exists
    IF EXISTS (SELECT 1 FROM information_schema.tables WHERE table_name = 'audit_logs') THEN
        IF TG_OP = 'DELETE' THEN
            INSERT INTO audit_logs (table_name, record_id, action, old_data)
            VALUES (TG_TABLE_NAME, OLD.id, TG_OP, to_jsonb(OLD));
            RETURN OLD;
        ELSE
            INSERT INTO audit_logs (table_name, record_id, action, new_data, old_data)
            VALUES (TG_TABLE_NAME, NEW.id, TG_OP, to_jsonb(NEW),
                    CASE WHEN TG_OP = 'UPDATE' THEN to_jsonb(OLD) ELSE NULL END);
            RETURN NEW;
        END IF;
    END IF;
    -- If audit_logs doesn't exist, just return without error
    IF TG_OP = 'DELETE' THEN
        RETURN OLD;
    ELSE
        RETURN NEW;
    END IF;
END;
$$;


