CREATE FUNCTION public.bulk_create_event_tickets(p_event_id uuid, p_ticket_count integer, p_price_tiers jsonb DEFAULT '[{"count": 100, "price": 100.00, "section": "GA"}]'::jsonb) RETURNS TABLE(created_count integer, duration_ms numeric)
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
