    start_time TIMESTAMP;
    end_time TIMESTAMP;
    total_created INTEGER := 0;
    tier JSONB;
    batch_size INTEGER := 1000;
    i INTEGER;
    event_slug TEXT;
BEGIN
    start_time := clock_timestamp();
    
    -- Get event slug for unique ticket numbers
    SELECT slug INTO event_slug FROM events WHERE id = p_event_id;
    
