    EXECUTE 'ALTER TABLE tickets DISABLE TRIGGER trigger_audit_tickets';
    
    -- Create tickets for each price tier
    FOR tier IN SELECT * FROM jsonb_array_elements(p_price_tiers)
    LOOP
        FOR i IN 0..CEIL(((tier->>'count')::INTEGER - 1) / batch_size::FLOAT)
        LOOP
            INSERT INTO tickets (
                event_id,
                ticket_number,
                ticket_code,
                section,
                status,
                face_value,
                purchase_price,
                tenant_id
            )
            SELECT 
                p_event_id,
                UPPER(SUBSTRING(event_slug FROM 1 FOR 8)) || '-' || 
                    (tier->>'section') || '-' ||
                    LPAD((total_created + ROW_NUMBER() OVER())::TEXT, 6, '0'),
                uuid_generate_v1(),
                tier->>'section',
                'ACTIVE'::ticket_status,
                (tier->>'price')::NUMERIC,
                (tier->>'price')::NUMERIC,
                (SELECT tenant_id FROM events WHERE id = p_event_id)
            FROM generate_series(
                1, 
                LEAST(batch_size, (tier->>'count')::INTEGER - (i * batch_size))
            );
            
            total_created := total_created + 
                LEAST(batch_size, (tier->>'count')::INTEGER - (i * batch_size));
        END LOOP;
    END LOOP;
    
    -- Re-enable triggers
    EXECUTE 'ALTER TABLE tickets ENABLE TRIGGER trg_update_event_stats';
    EXECUTE 'ALTER TABLE tickets ENABLE TRIGGER trigger_audit_tickets';
    
    -- Update counts ONCE
    UPDATE events 
    SET 
        total_tickets = total_created,
        available_tickets = total_created,
        updated_at = NOW()
    WHERE id = p_event_id;
    
    end_time := clock_timestamp();
    
    -- Return results
    created_count := total_created;
    duration_ms := EXTRACT(MILLISECONDS FROM (end_time - start_time));
    
    RETURN NEXT;
END;
$$;


