CREATE FUNCTION public.create_venue_tickets(p_event_id uuid, p_venue_capacity integer, p_tenant_id uuid) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
    batch_size INT := 1000;
    batches INT;
    i INT;
BEGIN
    batches := CEIL(p_venue_capacity::FLOAT / batch_size);
    
    FOR i IN 0..(batches-1) LOOP
        INSERT INTO tickets (event_id, ticket_number, status, face_value, purchase_price, tenant_id)
        SELECT 
            p_event_id,
            'SEAT-' || LPAD(((i * batch_size) + generate_series)::text, 6, '0'),
            'ACTIVE',
            100.00,
            100.00,
            p_tenant_id
        FROM generate_series(1, LEAST(batch_size, p_venue_capacity - (i * batch_size)));
    END LOOP;
    
    RAISE NOTICE 'Created % tickets for event', p_venue_capacity;
END;
$$;


