--
-- Name: generate_ticket_number(uuid, uuid); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.generate_ticket_number(p_event_id uuid, p_ticket_type_id uuid) RETURNS character varying
    LANGUAGE plpgsql
    AS $_$
DECLARE
    v_event_code TEXT;
    v_type_code TEXT;
    v_sequence INTEGER;
    v_ticket_number TEXT;
BEGIN
    SELECT UPPER(LEFT(REGEXP_REPLACE(name, '[^A-Za-z0-9]', '', 'g'), 3)) ||
           RIGHT(REPLACE(id::TEXT, '-', ''), 4)
    INTO v_event_code
    FROM events.events
    WHERE id = p_event_id;
    
    SELECT UPPER(LEFT(REGEXP_REPLACE(name, '[^A-Za-z0-9]', '', 'g'), 2))
    INTO v_type_code
    FROM tickets.ticket_types
    WHERE id = p_ticket_type_id;
    
    SELECT COALESCE(MAX(SUBSTRING(ticket_number FROM '[0-9]+$')::INTEGER), 0) + 1
    INTO v_sequence
    FROM tickets.tickets
    WHERE event_id = p_event_id AND ticket_type_id = p_ticket_type_id;
    
    v_ticket_number := v_event_code || '-' || COALESCE(v_type_code, 'GA') || '-' || LPAD(v_sequence::TEXT, 6, '0');
    
    RETURN v_ticket_number;
END;
$_$;


