        (SELECT jsonb_object_agg(country_code, cnt) 
         FROM (SELECT country_code, COUNT(*) as cnt FROM venues WHERE deleted_at IS NULL GROUP BY country_code) t) as venues_by_country,
        AVG(total_capacity) as average_capacity,
