    LANGUAGE plpgsql
    AS $$
DECLARE
    revoked_count INTEGER := 0;
    session_record RECORD;
BEGIN
    FOR session_record IN
        SELECT id FROM sessions
        WHERE user_id = p_user_id
        AND session_status = 'active'
        AND (p_except_session_id IS NULL OR id != p_except_session_id)
    LOOP
