CREATE FUNCTION public.test_ticket_insert_performance() RETURNS TABLE(test_name text, duration_ms bigint)
    LANGUAGE plpgsql
    AS $$
DECLARE
    start_ts TIMESTAMP;
    end_ts TIMESTAMP;
    test_event_id UUID;
    test_tenant_id UUID;
BEGIN
    -- Get test data
    SELECT id INTO test_event_id FROM events LIMIT 1;
    SELECT id INTO test_tenant_id FROM tenants WHERE slug = 'default' LIMIT 1;
    
    -- Test 1: Normal insert with all triggers
    start_ts := clock_timestamp();
    INSERT INTO tickets (event_id, ticket_number, status, face_value, purchase_price, tenant_id)
    VALUES (test_event_id, 'PERF-NORMAL-' || gen_random_uuid(), 'ACTIVE', 100.00, 100.00, test_tenant_id);
    end_ts := clock_timestamp();
    test_name := 'With all triggers';
    duration_ms := EXTRACT(MILLISECONDS FROM (end_ts - start_ts))::BIGINT;
    RETURN NEXT;
    
    -- Test 2: Disable user triggers
    ALTER TABLE tickets DISABLE TRIGGER USER;
    
    start_ts := clock_timestamp();
    INSERT INTO tickets (event_id, ticket_number, status, face_value, purchase_price, tenant_id)
    VALUES (test_event_id, 'PERF-NOTRIG-' || gen_random_uuid(), 'ACTIVE', 100.00, 100.00, test_tenant_id);
    end_ts := clock_timestamp();
    test_name := 'Without triggers';
    duration_ms := EXTRACT(MILLISECONDS FROM (end_ts - start_ts))::BIGINT;
    RETURN NEXT;
    
    -- Re-enable triggers
    ALTER TABLE tickets ENABLE TRIGGER USER;
    
    -- Clean up test data
    DELETE FROM tickets WHERE ticket_number LIKE 'PERF-%';
    
    RETURN;
END;
$$;


