CREATE FUNCTION public.update_event_metadata() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
BEGIN
    -- Update timestamp
    NEW.updated_at = NOW();
    
    -- Auto-generate slug if not provided
    IF NEW.slug IS NULL OR NEW.slug = '' THEN
        NEW.slug = lower(regexp_replace(NEW.name, '[^a-zA-Z0-9]+', '-', 'g'));
        NEW.slug = trim(NEW.slug, '-');
        
        -- Ensure uniqueness
        WHILE EXISTS (SELECT 1 FROM events.events WHERE slug = NEW.slug AND id != NEW.id) LOOP
            NEW.slug = NEW.slug || '-' || floor(random() * 1000)::text;
        END LOOP;
    END IF;
    
    -- Set publication timestamp
    IF OLD.status != 'PUBLISHED' AND NEW.status = 'PUBLISHED' AND NEW.published_at IS NULL THEN
        NEW.published_at = NOW();
    END IF;
    
    RETURN NEW;
END;
$$;


