--
-- Name: customer_engagement; Type: VIEW; Schema: analytics_v2; Owner: postgres
--

CREATE VIEW analytics_v2.customer_engagement AS
 SELECT cp.id AS customer_id,
    cp.email,
    count(DISTINCT t.id) AS total_tickets_purchased,
    count(DISTINCT e.id) AS unique_events_attended,
    count(DISTINCT e.venue_id) AS unique_venues_visited,
    sum(p.amount) AS lifetime_spent,
    min(t.created_at) AS first_purchase,
    max(t.created_at) AS last_purchase,
    (CURRENT_DATE - (max(t.created_at))::date) AS days_since_last_purchase,
        CASE
            WHEN (max(t.created_at) > (CURRENT_TIMESTAMP - '90 days'::interval)) THEN 'active'::text
            WHEN (max(t.created_at) > (CURRENT_TIMESTAMP - '180 days'::interval)) THEN 'at_risk'::text
            ELSE 'churned'::text
        END AS customer_status
   FROM ((((customers.customer_profiles cp
     LEFT JOIN tickets.tickets t ON (((t.owner_id = cp.id) OR (t.original_purchaser_id = cp.id))))
     LEFT JOIN tickets.ticket_types tt ON ((t.ticket_type_id = tt.id)))
     LEFT JOIN events.events e ON ((tt.event_id = e.id)))
     LEFT JOIN payments.transactions p ON ((p.order_id = t.order_id)))
  GROUP BY cp.id, cp.email;


