CREATE VIEW analytics_v2.event_sales_performance AS
 SELECT e.id AS event_id,
    e.name AS event_name,
    es.starts_at AS event_date,
    v.name AS venue_name,
    sum(tt.total_supply) AS total_capacity,
    count(DISTINCT t.id) AS tickets_sold,
    (sum(tt.total_supply) - count(DISTINCT t.id)) AS tickets_available,
        CASE
            WHEN (sum(tt.total_supply) > 0) THEN round(((100.0 * (count(DISTINCT t.id))::numeric) / (sum(tt.total_supply))::numeric), 2)
            ELSE (0)::numeric
        END AS sell_through_rate,
    sum(p.amount) AS total_revenue,
    avg(p.amount) AS avg_ticket_price,
    count(DISTINCT COALESCE(t.owner_id, t.original_purchaser_id)) AS unique_buyers,
    ((es.starts_at)::date - CURRENT_DATE) AS days_until_event
   FROM (((((events.events e
     JOIN venues.venues v ON ((e.venue_id = v.id)))
     LEFT JOIN events.event_schedules es ON (((es.event_id = e.id) AND (es.occurrence_number = 1))))
     LEFT JOIN tickets.ticket_types tt ON ((tt.event_id = e.id)))
     LEFT JOIN tickets.tickets t ON ((t.ticket_type_id = tt.id)))
     LEFT JOIN payments.transactions p ON ((p.order_id = t.order_id)))
  WHERE (e.deleted_at IS NULL)
  GROUP BY e.id, e.name, es.starts_at, v.name;


