-- Name: aml_monitoring_dashboard; Type: VIEW; Schema: compliance; Owner: postgres
--

CREATE VIEW compliance.aml_monitoring_dashboard AS
 SELECT check_status,
    risk_level,
    count(*) AS total_checks,
    count(*) FILTER (WHERE (created_at >= CURRENT_DATE)) AS today_checks,
    count(*) FILTER (WHERE (created_at >= (CURRENT_DATE - '7 days'::interval))) AS week_checks,
    count(*) FILTER (WHERE (sar_filed = true)) AS sars_filed,
    count(*) FILTER (WHERE (investigation_status IS NOT NULL)) AS under_investigation,
    avg(risk_score) AS avg_risk_score,
    max(risk_score) AS max_risk_score
   FROM compliance.aml_checks
  WHERE (created_at >= (CURRENT_DATE - '30 days'::interval))
  GROUP BY check_status, risk_level
  ORDER BY
        CASE risk_level
            WHEN 'critical'::text THEN 1
            WHEN 'high'::text THEN 2
            WHEN 'medium'::text THEN 3
            WHEN 'low'::text THEN 4
            ELSE NULL::integer
        END, check_status;


