--

CREATE VIEW compliance.compliance_report_dashboard AS
 SELECT report_type,
    status,
    count(*) AS total_reports,
    count(*) FILTER (WHERE ((due_date <= (CURRENT_DATE + '7 days'::interval)) AND ((status)::text <> ALL ((ARRAY['submitted'::character varying, 'acknowledged'::character varying])::text[])))) AS due_soon,
    count(*) FILTER (WHERE ((due_date < CURRENT_DATE) AND ((status)::text <> ALL ((ARRAY['submitted'::character varying, 'acknowledged'::character varying])::text[])))) AS overdue,
    count(*) FILTER (WHERE (created_at >= (CURRENT_DATE - '30 days'::interval))) AS recent_reports,
    count(*) FILTER (WHERE (is_amendment = true)) AS amendments,
    sum(findings_count) AS total_findings,
    sum(critical_findings) AS total_critical_findings
   FROM compliance.compliance_reports
  WHERE (created_at >= (CURRENT_DATE - '90 days'::interval))
  GROUP BY report_type, status
  ORDER BY report_type,
        CASE status
            WHEN 'draft'::text THEN 1
            WHEN 'pending_review'::text THEN 2
            WHEN 'approved'::text THEN 3
            WHEN 'submitted'::text THEN 4
            WHEN 'acknowledged'::text THEN 5
            WHEN 'rejected'::text THEN 6
            ELSE NULL::integer
        END;


