-- Name: current_kyc_status; Type: VIEW; Schema: compliance; Owner: postgres
--

CREATE VIEW compliance.current_kyc_status AS
 SELECT DISTINCT ON (customer_profile_id) customer_profile_id,
    id AS kyc_record_id,
    verification_level,
    verification_status,
    verified_at,
    expires_at,
    risk_score,
        CASE
            WHEN (((verification_status)::text = 'approved'::text) AND ((expires_at IS NULL) OR (expires_at > CURRENT_TIMESTAMP))) THEN 'valid'::text
            WHEN (((verification_status)::text = 'approved'::text) AND (expires_at <= CURRENT_TIMESTAMP)) THEN 'expired'::text
            WHEN ((verification_status)::text = 'pending'::text) THEN 'pending'::text
            WHEN ((verification_status)::text = 'rejected'::text) THEN 'rejected'::text
            ELSE 'none'::text
        END AS kyc_status
   FROM compliance.kyc_records
  ORDER BY customer_profile_id, created_at DESC;


