-- Name: data_retention_dashboard; Type: VIEW; Schema: compliance; Owner: postgres
--

CREATE VIEW compliance.data_retention_dashboard AS
 SELECT policy_type,
    sensitivity_level,
    count(*) AS total_policies,
    count(*) FILTER (WHERE is_active) AS active_policies,
    count(*) FILTER (WHERE legal_hold) AS legal_holds,
    count(*) FILTER (WHERE (next_purge_date <= (CURRENT_DATE + '30 days'::interval))) AS purge_upcoming,
    count(*) FILTER (WHERE ((compliance_status)::text = 'non_compliant'::text)) AS non_compliant,
    sum(records_purged_count) AS total_records_purged
   FROM compliance.data_retention
  GROUP BY policy_type, sensitivity_level
  ORDER BY policy_type,
        CASE sensitivity_level
            WHEN 'restricted'::text THEN 1
            WHEN 'confidential'::text THEN 2
            WHEN 'internal'::text THEN 3
            WHEN 'public'::text THEN 4
            ELSE NULL::integer
        END;


