-- Name: sanctions_monitoring_summary; Type: VIEW; Schema: compliance; Owner: postgres
--

CREATE VIEW compliance.sanctions_monitoring_summary AS
 SELECT screening_status,
    count(*) AS total_screenings,
    count(*) FILTER (WHERE (created_at >= CURRENT_DATE)) AS today_screenings,
    count(*) FILTER (WHERE ((screening_type)::text = 'onboarding'::text)) AS onboarding_checks,
    count(*) FILTER (WHERE ((screening_type)::text = 'periodic'::text)) AS periodic_checks,
    count(*) FILTER (WHERE ((screening_type)::text = 'triggered'::text)) AS triggered_checks,
    count(*) FILTER (WHERE (is_false_positive = true)) AS false_positives,
    count(*) FILTER (WHERE ((review_required = true) AND (reviewed_by IS NULL))) AS pending_reviews,
    avg(match_score) FILTER (WHERE (match_score > (0)::numeric)) AS avg_match_score,
    max(match_score) AS max_match_score
   FROM compliance.sanctions_screening
  WHERE (created_at >= (CURRENT_DATE - '30 days'::interval))
  GROUP BY screening_status
  ORDER BY
        CASE screening_status
            WHEN 'confirmed_match'::text THEN 1
            WHEN 'potential_match'::text THEN 2
            WHEN 'pending'::text THEN 3
            WHEN 'clear'::text THEN 4
            ELSE NULL::integer
        END;


