 SELECT id,
    tenant_id,
    integration_id,
    integration_name,
    mapping_name,
    mapping_version,
    is_active,
    source_entity,
    target_entity,
    sync_direction,
    field_mappings,
    source_data_type,
    target_data_type,
    type_conversion,
    transformation_function,
    custom_script,
    validation_rules,
    default_value_type,
    default_value,
    null_handling,
    conditions,
    conditional_mappings,
    value_lookup_table,
    enum_mappings,
    required_fields,
    unique_fields,
    validation_errors_action,
    date_format,
    number_format,
    timezone_handling,
    on_error_action,
    error_notification,
    is_indexed,
    cache_lookups,
    batch_size,
    test_data,
    last_tested_at,
    test_results,
    created_at,
    updated_at,
    created_by,
    approved_by,
    jsonb_array_length(field_mappings) AS field_count,
        CASE
            WHEN (last_tested_at IS NULL) THEN 'never_tested'::text
            WHEN (last_tested_at < (CURRENT_TIMESTAMP - '30 days'::interval)) THEN 'needs_testing'::text
            ELSE 'recently_tested'::text
        END AS test_status
   FROM integrations.integration_mappings im
  WHERE (is_active = true)
  ORDER BY integration_name, source_entity, target_entity;


