    account_name,
    is_active,
    verification_status,
    last_verified_at,
        CASE
            WHEN ((oauth_expires_at IS NOT NULL) AND (oauth_expires_at < CURRENT_TIMESTAMP)) THEN 'expired'::text
            WHEN ((oauth_expires_at IS NOT NULL) AND (oauth_expires_at < (CURRENT_TIMESTAMP + '7 days'::interval))) THEN 'expiring_soon'::text
