CREATE VIEW integrations.sync_statistics AS
 SELECT integration_name,
    sync_type,
    direction,
    count(*) AS total_syncs,
    count(*) FILTER (WHERE ((status)::text = 'completed'::text)) AS successful_syncs,
    count(*) FILTER (WHERE ((status)::text = 'failed'::text)) AS failed_syncs,
    count(*) FILTER (WHERE ((status)::text = 'running'::text)) AS running_syncs,
    avg(duration_seconds) FILTER (WHERE ((status)::text = 'completed'::text)) AS avg_duration_seconds,
    avg(records_per_second) FILTER (WHERE ((status)::text = 'completed'::text)) AS avg_records_per_second,
    sum(records_processed) AS total_records_processed,
    sum(api_credits_used) AS total_api_credits,
    sum(estimated_cost) AS total_estimated_cost,
    max(started_at) AS last_sync_at
   FROM integrations.sync_logs
  WHERE (created_at >= (CURRENT_DATE - '30 days'::interval))
  GROUP BY integration_name, sync_type, direction;


