--

CREATE VIEW integrations.webhook_health AS
 SELECT id,
    name,
    url,
    is_active,
    is_paused,
        CASE
            WHEN is_paused THEN 'circuit_open'::text
            WHEN (consecutive_failures > 0) THEN 'degraded'::text
            WHEN (last_success_at > (CURRENT_TIMESTAMP - '01:00:00'::interval)) THEN 'healthy'::text
            ELSE 'unknown'::text
        END AS health_status,
    consecutive_failures,
    failure_threshold,
    round((((successful_calls)::numeric / (NULLIF(total_calls, 0))::numeric) * (100)::numeric), 2) AS success_rate,
    average_response_time_ms,
    last_success_at,
    last_failure_at
   FROM integrations.webhook_endpoints;


