-- Name: active_campaigns; Type: VIEW; Schema: notifications; Owner: postgres
--

CREATE VIEW notifications.active_campaigns AS
 SELECT id,
    tenant_id,
    name,
    slug,
    description,
    type,
    status,
    audience_segment_ids,
    audience_count,
    audience_criteria,
    scheduled_at,
    started_at,
    completed_at,
    template_id,
    subject_line,
    preview_text,
    is_ab_test,
    variants,
    winning_variant_id,
    goal_type,
    goal_target,
    goal_achieved,
    budget_amount,
    spent_amount,
    cost_per_send,
    sends_count,
    opens_count,
    clicks_count,
    conversions_count,
    revenue_generated,
    suppression_list_ids,
    excluded_count,
    requires_approval,
    approved_by,
    approved_at,
    tags,
    metadata,
    created_at,
    updated_at,
    created_by,
        CASE
            WHEN (sends_count > 0) THEN round((((opens_count)::numeric / (sends_count)::numeric) * (100)::numeric), 2)
            ELSE (0)::numeric
        END AS open_rate,
        CASE
            WHEN (opens_count > 0) THEN round((((clicks_count)::numeric / (opens_count)::numeric) * (100)::numeric), 2)
            ELSE (0)::numeric
        END AS click_rate,
        CASE
            WHEN (budget_amount > (0)::numeric) THEN round(((spent_amount / budget_amount) * (100)::numeric), 2)
            ELSE (0)::numeric
        END AS budget_used_percentage
   FROM notifications.campaigns c
  WHERE ((status)::text = ANY ((ARRAY['scheduled'::character varying, 'active'::character varying])::text[]))
  ORDER BY scheduled_at;


