CREATE VIEW notifications.active_notification_preferences AS
 SELECT id,
    tenant_id,
    customer_profile_id,
    all_notifications_enabled,
    pause_until,
    email_enabled,
    sms_enabled,
    push_enabled,
    in_app_enabled,
    marketing_enabled,
    transactional_enabled,
    reminders_enabled,
    price_drops,
    new_events,
    artist_updates,
    venue_updates,
    max_emails_per_day,
    max_sms_per_week,
    quiet_hours_enabled,
    quiet_start,
    quiet_end,
    timezone,
    unsubscribed_all,
    unsubscribe_token,
    sms_country_code,
    push_sound_enabled,
    notification_language,
    batch_notifications,
    instant_notifications,
    last_email_open,
    last_sms_click,
    created_at,
    updated_at,
    last_modified_by,
        CASE
            WHEN ((pause_until IS NOT NULL) AND (pause_until > CURRENT_TIMESTAMP)) THEN true
            ELSE false
        END AS currently_paused,
        CASE
            WHEN (quiet_hours_enabled AND (((CURRENT_TIME AT TIME ZONE timezone) >= (quiet_start)::time with time zone) AND ((CURRENT_TIME AT TIME ZONE timezone) <= (quiet_end)::time with time zone))) THEN true
            ELSE false
        END AS in_quiet_hours
   FROM notifications.notification_preferences np
  WHERE ((all_notifications_enabled = true) AND (unsubscribed_all = false));


