CREATE VIEW notifications.delivery_metrics AS
 SELECT provider_name,
    status,
    count(*) AS count,
    avg(EXTRACT(epoch FROM (delivered_at - queued_at))) AS avg_delivery_time_seconds,
    sum(actual_cost) AS total_cost,
    sum(provider_credits_used) AS total_credits,
    count(*) FILTER (WHERE (bounce_type IS NOT NULL)) AS bounce_count,
    count(*) FILTER (WHERE ((bounce_type)::text = 'hard'::text)) AS hard_bounce_count,
    count(*) FILTER (WHERE (spam_score > (5)::numeric)) AS high_spam_count
   FROM notifications.delivery_tracking dt
  WHERE (created_at >= (CURRENT_DATE - '30 days'::interval))
  GROUP BY provider_name, status;


