--
-- Name: customer_feedback_summary; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.customer_feedback_summary AS
 SELECT type,
    count(*) AS total_feedback,
    count(*) FILTER (WHERE ((status)::text = 'published'::text)) AS published_count,
    count(*) FILTER (WHERE ((status)::text = 'pending'::text)) AS pending_count,
    count(*) FILTER (WHERE is_verified_purchase) AS verified_count,
    avg(overall_rating) FILTER (WHERE (overall_rating IS NOT NULL)) AS avg_rating,
    count(*) FILTER (WHERE (overall_rating >= 4)) AS positive_count,
    count(*) FILTER (WHERE (overall_rating <= 2)) AS negative_count,
    count(*) FILTER (WHERE (created_at >= (CURRENT_DATE - '7 days'::interval))) AS recent_count
   FROM customers.customer_feedback
  GROUP BY type;


ALTER VIEW public.customer_feedback_summary OWNER TO postgres;

