--
-- Name: v_available_tickets; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.v_available_tickets AS
 SELECT t.id,
    t.ticket_number,
    t.face_value,
    e.name AS event_name,
    e.slug AS event_slug,
    v.name AS venue_name,
    es.starts_at AS event_date
   FROM (((tickets.tickets t
     JOIN events.events e ON ((t.event_id = e.id)))
     JOIN venues.venues v ON ((e.venue_id = v.id)))
     LEFT JOIN events.event_schedules es ON ((e.id = es.event_id)))
  WHERE ((t.status = 'ACTIVE'::public.ticket_status) AND (t.is_valid = true) AND ((e.status)::text = ANY ((ARRAY['ON_SALE'::character varying, 'PUBLISHED'::character varying])::text[])));


ALTER VIEW public.v_available_tickets OWNER TO postgres;

--
-- Name: v_venue_analytics; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.v_venue_analytics AS
 SELECT v.id,
    v.name,
    v.slug,
    count(DISTINCT e.id) AS total_events,
    count(DISTINCT t.id) AS total_tickets,
    sum(
        CASE
            WHEN (t.status = 'REDEEMED'::public.ticket_status) THEN 1
            ELSE 0
        END) AS tickets_redeemed,
    sum(t.face_value) AS potential_revenue
   FROM ((venues.venues v
     LEFT JOIN events.events e ON ((v.id = e.venue_id)))
     LEFT JOIN tickets.tickets t ON ((e.id = t.event_id)))
  GROUP BY v.id, v.name, v.slug;


